let genre1 = Genre(category: "Thriller", movies: [Movie(title: "The Guilty", image: UIImage(named: "The Guilty")!, releasedYear: "2021", movieRating: "6.3", boxOffice: "50M", moviePlot: "A troubled police detective demoted to 911 operator duty scrambles to save a distressed caller during a harrowing day of revelations -- and reckonings.", cast: ["Jake Gyllenhaal","Riley Keough","Peter Sarsgaard"]), Movie(title: "No Exit", image : UIImage(named: "No Exit")!, releasedYear: "2022", movieRating: "6.1", boxOffice: "20M", moviePlot: "A college student, on her way home from visiting her mother, gets stuck with a group of people at a mountain rest stop during a blizzard. Things take a turn for the worse when the young woman discovers a kidnapped child in a car belonging to one of the people inside, putting the group in a terrifying life-or-death situation as they struggle to escape while trying to discover who among them is the kidnapper.", cast: ["Havana Rose Liu","Danny Ramirez","David Rysdahl"]), Movie(title: "The Night House", image: UIImage(named: "The Night House")!, releasedYear: "2020", movieRating: "6.5", boxOffice: "15.5M", moviePlot: "Reeling from the unexpected death of her husband, Beth (Rebecca Hall) is left alone in the lakeside home he built for her. She tries as best she can to keep together-but then the dreams come. Disturbing visions of a presence in the house call to her, beckoning with a ghostly allure. But the harsh light of day washes away any proof of a haunting. Against the advice of her friends, she begins digging into his belongings, yearning for answers.", cast: ["Rebecca Hall","Sarah Goldberg","Vondie Curtis-Hall"]), Movie(title: "The Weekend Away", image: UIImage(named: "The Weekend Away")!, releasedYear: "2022", movieRating: "5.6", boxOffice: "50M", moviePlot: "When her best friend vanishes during a girls trip to Croatia, a woman races to figure out what happened. However, each clue yields another unsettling deception.", cast: ["Leighton Meester","Christina Wolfe","Ziad Bakri"]), Movie(title: "Old", image: UIImage(named: "Old")!, releasedYear: "2021", movieRating: "5.8", boxOffice: "90.1M", moviePlot: "A thriller about a family on a tropical holiday who discover that the secluded beach where they are relaxing for a few hours is somehow causing them to age rapidly reducing their entire lives into a single day.", cast: ["Gael Garcia Bernal","Vicky Krieps","Rufus Sewell"])])

